import React, { useState, FormEvent } from 'react';

import './styles.css'
import PageHeader from '../../components/PageHeader';
import TeacherItem from '../../components/TeacherItem';
import Input from '../../components/Input';
import Select from '../../components/Select';
import api from '../../services/api';

const TeacherList = () => {

	const [subject, setSubject] = useState('');
	const [day, setDay] = useState('');
	const [time, setTime] = useState('');

	const searchNow = async(e: FormEvent) => {
		e.preventDefault();

		const response = await api.get('classes', {
			params: {
				subject,
				day,
				time
			}
		})

		console.log(response.data);
	}

	return (
		<div id="page-teacher-list" className="container">
			<PageHeader title="Estes são os Proffys disponíveis.">
				<form id="teacher-search" onSubmit={searchNow}>
				<Select name="subject" label="Matéria" value={subject} onChange={e => setSubject(e.target.value)} options={[
            { value: 'Artes', label: 'Artes' },
            { value: 'Biologia', label: 'Biologia' },
            { value: 'Educação física', label: 'Educação física' },
            { value: 'Física', label: 'Física' },
            { value: 'Geografia', label: 'Geografia' },
            { value: 'História', label: 'História' },
            { value: 'Matemática', label: 'Matemática' },
            { value: 'Português', label: 'Português' },
            { value: 'Química', label: 'Química' },
          ]}/>
					<Select name="day" label="Dia da semana" value={day} onChange={e => setDay(e.target.value)} options={[
						{ value: '0', label: 'Domingo' },
            { value: '1', label: 'Segunda-feira' },
            { value: '2', label: 'Terça-feira' },
            { value: '3', label: 'Quarta-feira' },
            { value: '4', label: 'Quinta-feira' },
            { value: '5', label: 'Sexta-feira' },
            { value: '6', label: 'Sábado' }
          ]}/>
					<Input type="time" name="time" label="Horário" value={time} onChange={e => setTime(e.target.value)} />
					<button type="submit">Buscar</button>
				</form>
			</PageHeader>

			<main>
				<TeacherItem/>
				<TeacherItem/>
				<TeacherItem/>
				<TeacherItem/>
			</main>
		</div>
	);
};

export default TeacherList;
