import React from 'react';
import whatsappIcon from '../../assets/images/icons/whatsapp.svg'

import './styles.css';

interface TeacherItemProps {
	class: {
		name: string,
		subject: string,
		avatar: string,
		bio: string,
		cost: number,
		whatsapp: string
	}
}

const TeacherItem: React.FC<TeacherItemProps> = ({ teacher }) => {
    return(
        <article className="teacher-item">
					<header>
						<img src="https://external-content.duckduckgo.com/iu/?u=http%3A%2F%2Ftriner.de%2Fwp-content%2Fuploads%2F2016%2F08%2FFilip_profile_circle.png&f=1&nofb=1" alt="foto"/>
						<div>
							<strong>{teacher.name}</strong>
							<span>{teacher.subject}</span>
						</div>
					</header>

					<p>
						{teacher.bio}
					</p>

					<footer>
						<p>
							Pre√ßo/hora
							<strong>{`R$ ${teacher.cost}`}</strong>
						</p>
						<button type="button">
							<img src={whatsappIcon} alt="whatsapp"/>
							Entrar em contato
						</button>
					</footer>
				</article>
    );
}

export default TeacherItem;
